const gcoord = require('./gcoord');
const {transform, WGS84, GCJ02, BD09} = require('./gcoord');

const wgs84_to_bd = (poi) => {
    return gcoord.transform(
        [poi.Lng, poi.Lat],
        WGS84,
        BD09
    )
};

const wgs84_to_gg = (poi) => {
    return gcoord.transform(
        [poi.Lng, poi.Lat],
        WGS84,
        GCJ02
    )
};

const gg_to_wgs84 = (poi) => {
    return gcoord.transform(
        [poi.Lng, poi.Lat],
        GCJ02,
        WGS84
    )
};

const bd_to_wgs84 = (poi) => {
    return gcoord.transform(
        [poi.Lng, poi.Lat],
        BD09,
        WGS84
    )
};

// console.log(wgs84_to_gg({Lat: 22.514256, Lng: 113.337651}))

module.exports = {
    wgs84_to_bd,
    wgs84_to_gg,
    gg_to_wgs84,
    bd_to_wgs84
}