Array.prototype.last = function () {
    let me = this;
    return me[me.length - 1];
};

Array.prototype.first = function () {
    let me = this;
    return me[0];
};

Array.prototype.findByField = function (field, value) {
    let me = this;
    let result = [];
    for (let i = 0; i < me.length; i++) {
        let _mi = me[i];
        if (_mi.hasOwnProperty(field) && _mi[field] === value) {
            result.push(_mi);
        }
    }
    return result;
};

Array.prototype.getFieldValues = function (key) {
    let me = this;
    let result = [];
    for (let i = 0; i < me.length; i++) {
        let _mi = me[i];
        if (_mi.hasOwnProperty(key)) {
            result.push(_mi[key]);
        }
    }
    return result;
};

Array.prototype.joinFieldValues = function (key) {
    let me = this;
    let result = [];
    for (let i = 0; i < me.length; i++) {
        let _mi = me[i];
        if (_mi.hasOwnProperty(key)) {
            result.push(_mi[key]);
        }
    }
    return result.join(",");
};

/***
 * 批量设置数组内某一个字段
 * @param key
 * @param value
 */
Array.prototype.setAllKey = function (key, value) {
    let array = this;
    let result = [];
    for (let i = 0; i < array.length; i++) {
        let tmp = array[i];
        tmp[key] = value;
        result.push(tmp);
    }
    return result;
};

/***
 * 插入排序，适用于比较小的数组。复杂度为O(N*(N-1))
 * @constructor
 */
Array.prototype.InsertSort = function (key) {
    let array = this;
    let len = array.length,
        i, j, tmp, result;

    // 设置数组副本
    result = array.slice(0);
    for (i = 1; i < len; i++) {
        tmp = result[i];
        j = i - 1;
        while (j >= 0 && tmp[key] < result[j][key]) {
            result[j + 1] = result[j];
            j--;
        }
        result[j + 1] = tmp;
    }
    return result;
};

router.ProGetLastSingle = function (fn, sn) {
    return new Promise(function (resolve, reject) {
        let url = `${myUtil.RES_REDIS_HOST}/${fn}/single/${sn}`;
        request(url, function (err, res, body) {
            if (err) {
                console.log(`${url} -> ERROR : ${err.message}`);
                reject(err);
            } else if (res.statusCode > 399 || !body) {
                console.log(`${url} : ${res.statusCode}`);
                reject(body, res.statusCode);
            } else {
                if (typeof body === 'string' && (body[0] === '{' || body[0] === '['))
                    body = JSON.parse(body);
                resolve(body);
            }
        });
    });
};

/***
 * 从SRC复制到TAR ,
 * @param src
 * @param tar
 * @constructor
 * @return {{}}
 */
router.Clone = function (src, tar) {
    let clone = {};
    if (!tar) return null;
    for (let k in src) {
        if (src.hasOwnProperty(k))
            if (!!tar[k] && typeof(src[k]) === 'number') {
                clone[k] = isNaN(tar[k]) ? 0 : tar[k];
            } else {
                clone[k] = tar[k] || src[k];
            }
    }
    return clone;
};

router.ClassClone = function (src, tar, res) {
    let clone = {};
    for (let k in src) {
        if (src.hasOwnProperty(k)) {
            let m = src[k];
            if (m === REQUIRED && !tar[k]) {
                res.send(204, k + "is required");
                return null;
            }
            clone[k] = tar[k] || src[k];
        }
    }
    return clone;
};

router.Get = function (url, cb) {
    debug && console.log("GET : " + url);
    request(url, function (err, response, body) {
        if (err) console.log(err);
        if (err || (response && response.statusCode > 400)) {
            console.log(url);
            console.log(response && response.statusCode + " : " + body);
        }
        try {
            cb && cb(err, response, body);
        } catch (e) {
            console.log(e);
        }
    });
};

router.PromiseGet = function (url) {
    debug && console.log(`${debug} -- GET : ${url}`);
    let _url = encodeURI(url);
    let fn = (done, reject) => {
        request(_url, function (err, res, body) {
            if (err) reject(err, res, body);
            else done(body);
        });
    };
    return new Promise(fn);
};

